SELECT name
FROM config.tag_tables
WHERE name IN (
    SELECT unnest(
            string_to_array(
                replace(replace(replace(tag_tables, '[', ''), ']', ''), '"', ''), ','
            )
        )
    FROM config.endpoints
    WHERE name = '${endpoint}'
);

