}

msg.dashboard.loading = false;

return msg;

msg.query = `

    SELECT column_name 
    FROM information_schema.columns 
    WHERE table_schema = '${msg.database.schema}'
    AND table_name = '${msg.database.table}';

    SELECT * 
    FROM ${msg.database.schema}.${msg.database.table}
    ORDER BY id;

`;

msg.target = msg.database.name;
msg.topic = "select_data";
return msg;

// Extract schema and table names from the message
const schemaName = msg.database.schema;
const tableName = msg.database.table;

// Construct the dynamic query for creating the schema and the table
msg.query = `
DO $$
BEGIN
   -- Check if schema exists and create if it doesn't
   IF NOT EXISTS (
      SELECT 1
      FROM information_schema.schemata
      WHERE schema_name = '${schemaName}'
   ) THEN
      CREATE SCHEMA ${schemaName};
   END IF;

   -- Check if table exists and create if it doesn't
   IF NOT EXISTS (
      SELECT 1
      FROM information_schema.tables
      WHERE table_schema = '${schemaName}' AND table_name = '${tableName}'
   ) THEN
      CREATE TABLE ${schemaName}.${tableName} (
        id SERIAL,
        name TEXT PRIMARY KEY,
        vendor TEXT,
        details TEXT
      );
   END IF;
END $$;
`;

msg.target = msg.database.name;
msg.topic = "check_table";
return msg;

function escapeValue(value) {
    if (value === null || value === undefined) {
        // Handles NULL values
        return 'NULL';
    } 
    else if (Array.isArray(value) || typeof value === 'object') {
        // Convert arrays and objects to JSON, then escape single quotes
        const jsonString = JSON.stringify(value);
        return `'${jsonString.replace(/'/g, "''")}'`;
    }
    else if (typeof value === 'string') {
        // Escapes dangerous characters like single quotes ('), 
        // replacing them with double single quotes ('')
        return `'${value.replace(/'/g, "''")}'`;
    } 
    else if (typeof value === 'boolean') {
        // PostgreSQL uses 'TRUE'/'FALSE' for boolean values
        return value ? 'TRUE' : 'FALSE';
    } 
    else {
        // For numbers (and other primitive types), return the value directly
        return value;
    }
}


let commands = [];
const table = msg.database.table;
const schema = msg.database.schema;

for (const change of msg.dashboard.history) {
    const { index, oldItem, newItem } = change;

    if (oldItem && !newItem) {
        // Case: deletion
        commands.push(`DELETE FROM "${schema}"."${table}"WHERE name = ${escapeValue(oldItem.name)};`);

    } else if (!oldItem && newItem) {
        // Case: insertion
        const keys = Object.keys(newItem);
        const values = Object.values(newItem)
            .map(value => `${escapeValue(value)}`) // Escape values
            .join(', ');
        commands.push(`INSERT INTO "${schema}"."${table}" (${keys.join(', ')}) VALUES (${values});`);

    } else if (oldItem && newItem) {
        // Case: update
        const updates = Object.keys(newItem)
            .map(key => `${key} = ${escapeValue(newItem[key])}`) // Escape values
            .join(', ');
        commands.push(`UPDATE "${schema}"."${table}" SET ${updates} WHERE name = ${escapeValue(oldItem.name)};`);
    }
}

// Constructs the PL/pgSQL block
msg.query = `
DO $$
BEGIN
    ${commands.join('\n')}
END $$;
`;

// Resets the dashboard history
msg.dashboard.history = [];

msg.target = msg.database.name;
msg.topic = 'deploy_changes';
return msg;

return {
    topic: (msg.topicMain === 'start') ? 'start' : 'update',
    time: new Date(),
    _msgid: msg._msgid
};

